class UserServices {
    constructor(axios) {
        this.axios = axios;
    }

    get() {
        return this.axios.get(`/?randomapi`).then(res => {
            return res.data
        });
    }

    getDetail(id) {
        return this.axios.get(`/user/${id}`).then(res => res.data)
    }

    creat(book) {
        return this.axios.post(`/user`, book).then(res => res.data)
    }

    edit(id, book) {
        return this.axios.put(`/user/${id}`, book).then(res => res.data)
    }

    filter(params) {
        return this.axios.get(`/user`, {params: params}).then(res => res.data)
    }
}

export default UserServices;
