import React, {Component} from 'react';
import SwipeItem from './SwipeItem'
import {userServices} from "../services";

class SwipeList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: {}
        };
    }

    async componentWillMount() {
        let data = await userServices.get();
        this.setState({
            data: data
        })
    }

    addImage() {
        this.setState({
            counter: this.state.counter + 1,
            items: {...this.state.items, [this.state.counter]: 'http://lorempixel.com/350/65/'}
        });
    }

    removeItem(keyOfItemToRemove) {
        let nextItems = {};
        Object.keys(this.state.items).forEach(itemKey => {
            if (itemKey !== keyOfItemToRemove) {
                nextItems[itemKey] = this.state.items[itemKey];
            }
        });

        this.setState({items: nextItems});
    }

    render() {
        return (
            <React.Fragment>
                {Object.keys(this.state.data).map(el => {
                    const user = el.user;
                        return (<SwipeItem key={`swipeItem-${user.username}`} onRemoval={() => this.removeItem(user.username)}>
                            <div className="cardheader">
                            </div>
                            <div className="avatar">
                                <div className="wrap-image">
                                    <img alt="user"
                                         src={user.picture}/>
                                </div>
                            </div>
                            <div className="info">
                                <div className="desc">My address is</div>
                                <div className="title">
                                    <div style={{textTransform: 'capitalize'}}>{user.location.street}</div>
                                </div>
                            </div>
                            <div className="bottom d-flex justify-content-around">
                                <i className="fas fa-user-circle"/>
                                <i className="far fa-list-alt"/>
                                <i className="fas fa-map-marked-alt"/>
                                <i className="fas fa-phone"/>
                                <i className="fas fa-lock"/>
                            </div>
                        </SwipeItem>)
                    }
                )}
            </React.Fragment>
        );
    }
}

export default SwipeList
