import React, {Component} from 'react';
import SwipeItem from './SwipeItem'
import {userServices} from "../services";

class SwipeList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: []
        };
    }

    async componentWillMount() {
        let data = await userServices.get();
        this.setState({
            data: data
        })
    }

    removeItem(username) {
        let nextItems = {};
        this.state.data.forEach(el => {
            if (el.username !== username) {
                nextItems[el.username] = this.state.data[el.username];
            }
        });

        this.setState({items: nextItems});
    }

    render() {
        return (
            <React.Fragment>
                {this.state.data.map(el =>
                    <SwipeItem key={`swipeItem-${el.username}`} onRemoval={() => this.removeItem(el.username)}>
                        <div className="cardheader">
                        </div>
                        <div className="avatar">
                            <div className="wrap-image">
                                <img alt="user"
                                     src="https://cdn4.iconfinder.com/data/icons/small-n-flat/24/user-alt-512.png"/>
                            </div>
                        </div>
                        <div className="info">
                            <div className="desc">My address is</div>
                            <div className="title">
                                <div>4661 Auburn Ave</div>
                            </div>
                        </div>
                        <div className="bottom d-flex justify-content-around">
                            <i className="fas fa-user-circle"/>
                            <i className="far fa-list-alt"/>
                            <i className="fas fa-map-marked-alt"/>
                            <i className="fas fa-phone"/>
                            <i className="fas fa-lock"/>
                        </div>
                    </SwipeItem>
                )}
            </React.Fragment>
        );
    }
}

export default SwipeList
